mapper.select:
select rn, med_id, med_name, med_weight, med_usage, med_amount, member_hospital_id
from(
	select rownum as rn, med_id, med_name, med_weight, med_usage, med_amount, member_hospital_id
	from(
		select med_id, med_name, med_weight, med_usage, med_amount, member_hospital_id 
		from medicine order by med_id
	)
	where rownum <= #{endRowNo}
)
where rn >= #{startRowNo}
