mapper.select:
select rn, order_id, order_need_time, order_agency_id, order_date, order_status 
from(
	select rownum as rn, order_id, order_need_time, order_agency_id, order_date, order_status
	from(
		select order_id, order_need_time, order_agency_id, order_date, order_status
		from request where order_agency_id=#{agency_id} order by order_need_time, order_id
	)
	where rownum <= #{endRowNo}
)
where rn >= #{startRowNo}

select rn, order_id, order_need_time, order_agency_id, order_date, order_status 
from(
	select rownum as rn, order_id, order_need_time, order_agency_id, order_date, order_status
	from(
		select order_id, order_need_time, order_agency_id, order_date, order_status
		from request order by order_need_time, order_id
	)
	where rownum <= #{endRowNo}
)
where rn >= #{startRowNo}


select rn, ORDER_ID, ORDER_AGENCY_ID, AGENCY_NAME, AGENCY_LATITUDE, AGENCY_LONGITUDE, ORDER_NEED_TIME
from(
	select rownum as rn, ORDER_ID,  ORDER_AGENCY_ID, AGENCY_NAME, AGENCY_LATITUDE, AGENCY_LONGITUDE, ORDER_NEED_TIME
	from(
		select ORDER_ID, ORDER_AGENCY_ID, AGENCY_NAME, AGENCY_LATITUDE, AGENCY_LONGITUDE, ORDER_NEED_TIME
		from request join agency
		on request.ORDER_AGENCY_ID = agency.AGENCY_ID
		where ORDER_STATUS = 'PREPARING'
		order by order_need_time, order_id
	)
	where rownum <= #{endRowNo}
)
where rn >= #{startRowNo}
